CREATE TABLE IF NOT EXISTS "migrations" ("id" integer primary key autoincrement not null, "migration" varchar not null, "batch" integer not null);
CREATE TABLE IF NOT EXISTS "users" ("id" integer primary key autoincrement not null, "name" varchar not null, "email" varchar not null, "email_verified_at" datetime, "password" varchar not null, "remember_token" varchar, "created_at" datetime, "updated_at" datetime, "role" varchar not null default 'user', "profile_visibility" varchar check ("profile_visibility" in ('public', 'friends', 'private')) not null default 'public', "posts_visibility" varchar check ("posts_visibility" in ('public', 'friends')) not null default 'public', "banned_at" datetime, "suspended_at" datetime, "suspension_ends_at" datetime, "suspension_reason" text, "two_factor_enabled" tinyint(1) not null default '0', "two_factor_secret" text, "two_factor_recovery_codes" text, "deactivated_at" datetime, "notification_preferences" text);
CREATE UNIQUE INDEX "users_email_unique" on "users" ("email");
CREATE TABLE IF NOT EXISTS "password_reset_tokens" ("email" varchar not null, "token" varchar not null, "created_at" datetime, primary key ("email"));
CREATE TABLE IF NOT EXISTS "failed_jobs" ("id" integer primary key autoincrement not null, "uuid" varchar not null, "connection" text not null, "queue" text not null, "payload" text not null, "exception" text not null, "failed_at" datetime not null default CURRENT_TIMESTAMP);
CREATE UNIQUE INDEX "failed_jobs_uuid_unique" on "failed_jobs" ("uuid");
CREATE TABLE IF NOT EXISTS "personal_access_tokens" ("id" integer primary key autoincrement not null, "tokenable_type" varchar not null, "tokenable_id" integer not null, "name" varchar not null, "token" varchar not null, "abilities" text, "last_used_at" datetime, "expires_at" datetime, "created_at" datetime, "updated_at" datetime);
CREATE INDEX "personal_access_tokens_tokenable_type_tokenable_id_index" on "personal_access_tokens" ("tokenable_type", "tokenable_id");
CREATE UNIQUE INDEX "personal_access_tokens_token_unique" on "personal_access_tokens" ("token");
CREATE TABLE IF NOT EXISTS "profiles" ("id" integer primary key autoincrement not null, "user_id" integer not null, "bio" varchar, "avatar" varchar, "created_at" datetime, "updated_at" datetime, "location" varchar, foreign key("user_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "posts" ("id" integer primary key autoincrement not null, "user_id" integer not null, "content" text not null, "created_at" datetime, "updated_at" datetime, "pet_id" integer, foreign key("user_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "comments" ("id" integer primary key autoincrement not null, "user_id" integer not null, "post_id" integer not null, "content" text not null, "created_at" datetime, "updated_at" datetime, "parent_id" integer, "deleted_at" datetime, foreign key("user_id") references "users"("id") on delete cascade, foreign key("post_id") references "posts"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "reactions" ("id" integer primary key autoincrement not null, "user_id" integer not null, "post_id" integer not null, "created_at" datetime, "updated_at" datetime, "type" varchar not null default 'like', foreign key("user_id") references "users"("id") on delete cascade, foreign key("post_id") references "posts"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "post_reports" ("id" integer primary key autoincrement not null, "user_id" integer not null, "post_id" integer not null, "reason" text not null, "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "users"("id") on delete cascade, foreign key("post_id") references "posts"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "comment_reports" ("id" integer primary key autoincrement not null, "user_id" integer not null, "comment_id" integer not null, "reason" text not null, "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "users"("id") on delete cascade, foreign key("comment_id") references "comments"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "tags" ("id" integer primary key autoincrement not null, "name" varchar not null, "created_at" datetime, "updated_at" datetime);
CREATE UNIQUE INDEX "tags_name_unique" on "tags" ("name");
CREATE TABLE IF NOT EXISTS "post_tag" ("post_id" integer not null, "tag_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("post_id") references "posts"("id") on delete cascade, foreign key("tag_id") references "tags"("id") on delete cascade, primary key ("post_id", "tag_id"));
CREATE TABLE IF NOT EXISTS "messages" ("id" integer primary key autoincrement not null, "sender_id" integer not null, "receiver_id" integer not null, "content" text not null, "read" tinyint(1) not null default '0', "created_at" datetime, "updated_at" datetime, foreign key("sender_id") references "users"("id") on delete cascade, foreign key("receiver_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "shares" ("id" integer primary key autoincrement not null, "user_id" integer not null, "post_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "users"("id") on delete cascade, foreign key("post_id") references "posts"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "friend_requests" ("id" integer primary key autoincrement not null, "sender_id" integer not null, "receiver_id" integer not null, "status" varchar check ("status" in ('pending', 'accepted', 'declined')) not null default 'pending', "created_at" datetime, "updated_at" datetime, "category" varchar, foreign key("sender_id") references "users"("id") on delete cascade, foreign key("receiver_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "follows" ("id" integer primary key autoincrement not null, "follower_id" integer not null, "following_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("follower_id") references "users"("id") on delete cascade, foreign key("following_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "pets" ("id" integer primary key autoincrement not null, "user_id" integer not null, "name" varchar not null, "type" varchar, "breed" varchar, "birthdate" date, "avatar" varchar, "created_at" datetime, "updated_at" datetime, "location" varchar, "bio" text, "favorite_food" varchar, "favorite_toy" varchar, "is_public" tinyint(1) not null default '1', foreign key("user_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "activity_logs" ("id" integer primary key autoincrement not null, "user_id" integer not null, "action" varchar not null, "description" varchar not null, "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "pet_friendships" ("id" integer primary key autoincrement not null, "pet_id" integer not null, "friend_pet_id" integer not null, "category" varchar, "created_at" datetime, "updated_at" datetime, "status" varchar not null default 'accepted', foreign key("pet_id") references "pets"("id") on delete cascade, foreign key("friend_pet_id") references "pets"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "blocks" ("id" integer primary key autoincrement not null, "blocker_id" integer not null, "blocked_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("blocker_id") references "users"("id") on delete cascade, foreign key("blocked_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "pet_activities" ("id" integer primary key autoincrement not null, "pet_id" integer not null, "activity_type" varchar not null, "description" text, "location" varchar, "happened_at" datetime not null, "image" varchar, "is_public" tinyint(1) not null default '1', "created_at" datetime, "updated_at" datetime, "type" varchar, "data" text, "actor_type" varchar, "actor_id" integer, "target_type" varchar, "target_id" integer, "read" tinyint(1) not null default '0', foreign key("pet_id") references "pets"("id") on delete cascade);
CREATE INDEX "pet_activities_pet_id_index" on "pet_activities" ("pet_id");
CREATE INDEX "pet_activities_activity_type_index" on "pet_activities" ("activity_type");
CREATE INDEX "pet_activities_happened_at_index" on "pet_activities" ("happened_at");
CREATE TABLE IF NOT EXISTS "user_activities" ("id" integer primary key autoincrement not null, "user_id" integer not null, "type" varchar, "description" text, "data" text, "actor_type" varchar, "actor_id" integer, "target_type" varchar, "target_id" integer, "read" tinyint(1) not null default '0', "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "groups" ("id" integer primary key autoincrement not null, "name" varchar not null, "description" text, "category" varchar, "visibility" varchar check ("visibility" in ('open', 'closed', 'secret')) not null default 'open', "creator_id" integer not null, "cover_image" varchar, "icon" varchar, "rules" text, "location" varchar, "created_at" datetime, "updated_at" datetime, foreign key("creator_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "group_members" ("id" integer primary key autoincrement not null, "group_id" integer not null, "user_id" integer not null, "role" varchar check ("role" in ('member', 'moderator', 'admin')) not null default 'member', "status" varchar check ("status" in ('pending', 'active', 'banned')) not null default 'active', "joined_at" datetime, "created_at" datetime, "updated_at" datetime, foreign key("group_id") references "groups"("id") on delete cascade, foreign key("user_id") references "users"("id") on delete cascade);
CREATE UNIQUE INDEX "group_members_group_id_user_id_unique" on "group_members" ("group_id", "user_id");
CREATE TABLE IF NOT EXISTS "group_topics" ("id" integer primary key autoincrement not null, "title" varchar not null, "content" text not null, "group_id" integer not null, "user_id" integer not null, "is_pinned" tinyint(1) not null default '0', "is_locked" tinyint(1) not null default '0', "has_solution" tinyint(1) not null default '0', "last_activity_at" datetime, "views_count" integer not null default '0', "created_at" datetime, "updated_at" datetime, foreign key("group_id") references "groups"("id") on delete cascade, foreign key("user_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "group_topic_replies" ("id" integer primary key autoincrement not null, "content" text not null, "group_topic_id" integer not null, "user_id" integer not null, "parent_id" integer, "is_solution" tinyint(1) not null default '0', "created_at" datetime, "updated_at" datetime, foreign key("group_topic_id") references "group_topics"("id") on delete cascade, foreign key("user_id") references "users"("id") on delete cascade, foreign key("parent_id") references "group_topic_replies"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "group_topic_participants" ("id" integer primary key autoincrement not null, "group_topic_id" integer not null, "user_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("group_topic_id") references "group_topics"("id") on delete cascade, foreign key("user_id") references "users"("id") on delete cascade);
CREATE UNIQUE INDEX "group_topic_participants_group_topic_id_user_id_unique" on "group_topic_participants" ("group_topic_id", "user_id");
CREATE TABLE IF NOT EXISTS "group_events" ("id" integer primary key autoincrement not null, "title" varchar not null, "description" text, "group_id" integer not null, "user_id" integer not null, "start_date" datetime not null, "end_date" datetime, "location" varchar, "location_url" varchar, "is_online" tinyint(1) not null default '0', "online_meeting_url" varchar, "cover_image" varchar, "max_attendees" integer, "is_published" tinyint(1) not null default '1', "created_at" datetime, "updated_at" datetime, foreign key("group_id") references "groups"("id") on delete cascade, foreign key("user_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "group_event_attendees" ("id" integer primary key autoincrement not null, "group_event_id" integer not null, "user_id" integer not null, "status" varchar check ("status" in ('going', 'interested', 'not_going')) not null default 'going', "reminder_set" tinyint(1) not null default '0', "created_at" datetime, "updated_at" datetime, foreign key("group_event_id") references "group_events"("id") on delete cascade, foreign key("user_id") references "users"("id") on delete cascade);
CREATE UNIQUE INDEX "group_event_attendees_group_event_id_user_id_unique" on "group_event_attendees" ("group_event_id", "user_id");
CREATE TABLE IF NOT EXISTS "group_categories" ("id" integer primary key autoincrement not null, "name" varchar not null, "slug" varchar not null, "icon" varchar, "color" varchar, "description" text, "display_order" integer not null default '0', "is_active" tinyint(1) not null default '1', "created_at" datetime, "updated_at" datetime);
